import React, { useState, useEffect } from "react";
import Contact from "./Contacts";
import contactsData from "./Contacts.json";
import "bootstrap/dist/css/bootstrap.min.css";


function App() {
  const [contacts, setContacts] = useState([]);

  useEffect(() => {
    const requestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch("127.0.0.1:5158/read", requestOptions)
      .then((response) => response.json())
      .then((result) => set(result))
      .catch((error) => console.log("error".error));
  }, []);
  // function App() {
  //   const [contacts, setContacts] = useState([]);

  //   useEffect(() => {
  //     setContacts(contactsData);
  //   }, []);

  const handleNoteChange = (id, event) => {
    const updatedContacts = contacts.map((contact) => {
      if (contact.id === id) {
        return {
          ...contact,
          notes: event.target.value,
        };
      }
      return contact;
    });
    setContacts(updatedContacts);
  };

  return (
    <div className="container">
      <h1 className="text-center mb-5">주소록</h1>
      <div className="row">
        {contacts.map((contact) => (
          <div key={contact.id} className="col-md-4 mb-4">
            <Contact contact={contact} />
            <div className="mb-3">
              <label htmlFor={`note-${contact.id}`} className="form-label">
                {`메모 추가하기 (${contact.firstName})`}
              </label>
              <input
                type="text"
                className="form-control"
                id={`note-${contact.id}`}
                value={contact.notes}
                onChange={(event) => handleNoteChange(contact.id, event)}
              />
              <button id="memo">추가</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
