import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, ListGroup, InputGroup, FormControl } from "react-bootstrap";
import Background from "./Background";

function App() {
  const [contacts, setContacts] = useState([]);

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:5158/read");
        const contactsWithMemo = response.data.map((contact) => ({
          ...contact,
          memo: "",
        }));
        setContacts(contactsWithMemo);
      } catch (error) {
        console.error("Error Error Error Error", error);
      }
    };

    fetchContacts();
  }, []);

  const updateMemo = (index, memo) => {
    const updatedContacts = [...contacts];
    updatedContacts[index].memo = memo;
    setContacts(updatedContacts);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-green-400 to-blue-500">
      <Background />
      <div className="container mx-auto p-4">
        <h1 className="text-4xl font-bold text-black my-8">전화번호부</h1>
        <div className="bg-white rounded shadow p-6">
          {contacts.map((contact, index) => (
            <div key={contact.id} className="border-b py-4">
              <p className="text-lg font-semibold">
                {contact.firstName} {contact.lastName} - {contact.phone}
              </p>
              <div className="mt-2">
                <input
                  type="text"
                  className="border rounded w-full py-2 px-3"
                  placeholder="메모 추가"
                  value={contact.memo}
                  onChange={(e) => updateMemo(index, e.target.value)}
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
